/**
  * @brief GUI主任务
  * @note 无
  * @param 无
  * @retval 无
  */
void MainTask(void)
{
	WM_HWIN hWin;
  WM_HWIN hItem;
  U32 temp = 0;
  
  /* 创建对话框 */
	hWin = CreateFramewin();	
  /* 获取子控件句柄 */
  hItem = WM_GetDialogItem(hWin, ID_TEXT_4);
	while(1)
	{
    /* 使用文本控件显示数值 */
		TEXT_SetDec(hItem, temp, 4, 0, 0, 0);
		temp++;
		if(temp > 9999)
		{
			temp = 0;
		}
    /* 延时100ms */
		GUI_Delay(100);
	}
}
