/**
  * @brief 对话框回调函数
  * @note 无
  * @param pMsg：消息指针
  * @retval 无
  */
static void _cbDialog(WM_MESSAGE * pMsg) {
  WM_HWIN hItem;
  static U16 progbar_value = 0;

  switch (pMsg->MsgId) {
  case WM_INIT_DIALOG:
    /* 初始化Framewin控件 */
    hItem = pMsg->hWin;
    FRAMEWIN_SetTitleHeight(hItem, 32);
    FRAMEWIN_SetText(hItem, "STemWIN@EmbedFire STM32F103");
    FRAMEWIN_SetFont(hItem, GUI_FONT_16B_ASCII);
    /* 初始化Progbar0 */
    hItem = WM_GetDialogItem(pMsg->hWin, ID_PROGBAR_0);
    PROGBAR_SetFont(hItem, GUI_FONT_COMIC24B_ASCII);
		PROGBAR_SetMinMax(hItem, 0, 100);
    break;
	case WM_PAINT:
		hItem = WM_GetDialogItem(pMsg->hWin, ID_PROGBAR_0);
		progbar_value = PROGBAR_GetValue(hItem);
    PROGBAR_SetValue(hItem, progbar_value+1);
		if(progbar_value == 100)
			PROGBAR_SetValue(hItem, 0);
		break;
  default:
    WM_DefaultProc(pMsg);
    break;
  }
}

